declare module "node-elm-compiler" {
  export interface Options {
    output?: string
    report?: "json"
    debug?: boolean
    optimize?: boolean
    verbose?: boolean
  }

  /**
   * Compiles a list of elm files into an output file
   */
  export function compile(inputFiles: string[], options: Options): Promise<void>

  /**
   * Compiles input files to a string, and optionally to an output file
   */
  export function compileToString(
    inputFiles: string[],
    options: Options,
  ): Promise<Buffer>

  // export function compileSync()
  // export function compileToStringSync()
  // export function compileWorker()
  // export function findAllDependencies()
  // export function _prepareProcessArgs()
}
